import React from "react";

export const ShowOnline = ({
  messageList,
  setMessage,
  sendMessage,
  userName,
  users,
  handleIn
}) => {
  console.log(messageList)
  return (
    <div className="popular_container">
      <div className="users">
        {users.filter(e=>e.name!==userName).map((user,key) => {
          const elements=JSON.stringify(user)
          return (
            <div onClick={handleIn} data-id={elements}  key={key}>
              <span style={{backgroundColor:user.status?`#5ff064`:'#798079'}}></span>
              <div>{user.name}</div>
            </div>
          );
        })}
      </div>

      <div className="chatContainer">
        <div className="messages">
          {messageList[0]?messageList.map((val, key) => {
            console.log('hii',)
            return (
              <div
              key={key}
                className="messageContainer"
                id={val.author == userName ?"Other": "You"}
              >
                <div className="messageIndividual">
                  {val.author}: {val.message}
                </div>
              </div>
            );
          }):''}
        </div>

        <div className="messageInputs">
          <input
            type="text"
            placeholder="Message..."
            onChange={(e) => {
              setMessage(e.target.value);
            }}
          />
          <button onClick={sendMessage}>Send</button>
        </div>
      </div>
    </div>
  );
};
