import React, { useState, useEffect } from "react";
import io from "socket.io-client";
import {ShowOnline} from './showOnline'
import "./App.css";

let socket;
const CONNECTION_PORT = "localhost:3002/";

function App() {
  // Before Login
  const [loggedIn, setLoggedIn] = useState(false);
  const [room, setRoom] = useState("");
  const [userName, setUserName] = useState("");

  // After Login
  const [message, setMessage] = useState("");
  const [messageList, setMessageList] = useState([]);
  const [userList,setUsers]=useState([{name:'viktoh',status:false,id:'ewfwgweagwae'},{name:'ikenna',status:true,id:'ewfwgweagwae'}]);

  useEffect(() => {
    socket = io(CONNECTION_PORT);
  }, [CONNECTION_PORT]);

  useEffect(() => {
    socket.on("receive_message", (data) => {
      setMessageList([...messageList, data]);
    });
  });
  useEffect(() => {
    socket.on("online_users", (data) => {
      // setMessageList([...messageList, data]);
      setUsers((prev)=>{
        return[...prev,data]
      })
    });
  },[]);
  useEffect(() => {
    socket.on("disconnected", (data) => {
      // setMessageList([...messageList, data]);
      // setUsers((prev)=>{
      //   return[...prev,data]
      // })
      userList.map(user=>{
        if(user.id===user){

        }
      })
      console.log(data)
    });
  },[]);
  const connectToRoom = () => {
    setLoggedIn(true);

   socket.emit("online", userName);
  };

  const sendMessage = async () => {
    let messageContent = {
      room: room,
      content: {
        author: userName,
        message: message,
      },
    };

    await socket.emit("send_message", messageContent);
    setMessageList([...messageList, messageContent.content]);
    setMessage("");
  };

  return (
    <div className="App">
      {!loggedIn ? (
        <div className="logIn">
          <div className="inputs">
            <input
              type="text"
              placeholder="Name..."
              onChange={(e) => {
                setUserName(e.target.value);
              }}
            />
          </div>
          <button onClick={connectToRoom}>Enter Chat</button>
        </div>
      ) : <ShowOnline messageList={messageList} users={userList} setMessage={setMessage} sendMessage={sendMessage} userName={userName}/>}
  </div>
  )}

export default App;
